@import "./base.scss";

.projects {

    &__header {
        font-size: 60px;
        font-style: italic;
        padding-bottom: 10px;
        font-family: 'Beam Weapon', sans-serif;
        font-family: 'Beam Weapon 3D', sans-serif;
        font-family: 'Beam Weapon Gradient', sans-serif;
        font-family: 'Beam Weapon Halftone', sans-serif;
        font-family: 'Beam Weapon Laser', sans-serif;
        font-family: 'Beam Weapon 3D Italic', sans-serif;
        font-family: 'Beam Weapon Condensed', sans-serif;
        font-family: 'Beam Weapon Condensed Italic', sans-serif;
        font-family: 'Beam Weapon Expanded', sans-serif;
        font-family: 'Beam Weapon Expanded Italic', sans-serif;
        font-family: 'Beam Weapon Gradient Italic', sans-serif;
        font-family: 'Beam Weapon Halftone Italic', sans-serif;
        font-family: 'Beam Weapon Italic', sans-serif;
        font-family: 'Beam Weapon Laser Italic', sans-serif;
        font-family: 'Beam Weapon Leftalic', sans-serif;
        font-family: 'Beam Weapon Super-Italic', sans-serif;
        color: $purple-electric;

    }
}

.project {
    &__header {
        display: grid;
        grid-template-columns: 1fr 3fr;
        gap: 1rem;
        font-family: sans-serif;

        & span {
            border-top: 1px solid #fff;
            padding: 1rem;
            font-size: 30px;
            text-align: center;


        }
    }

    &__infos {
        margin-top: 1rem;

        &--name {

            position: relative;
            font-size: 3vw;
            font-family: sans-serif;
            display: flex;
            align-items: center;
            font-weight: 400;
            
            
        }
    
    }

    &__img {
        position: relative;
        width: 90vw;
        height: 700px;

        img {
            width: 60vw;
            height: 600px;
            object-fit: cover;
            position: absolute;
            right: 10vw;
            top: 3rem;
        }

        .threeop {
            canvas {
                width: 60vw;
                height: 400px;

            }
        }

    }

    &__links {


        padding-top: 3rem;

    }

    &__tags {
        position: absolute;
        bottom: 1rem;
        display: flex;
        flex-direction: column;
        gap: 1rem;
    }

    .coolFunnyLink {
        position: relative;
        font-family: sans-serif;

        svg {
            position: absolute;
            bottom: -5px;
            left: 0;
            stroke-width: 1px;
            width: 95px;


        }


    }

    .coolFunnyLink path {
        stroke: $purple-electric;
        /* Assuming $purple-electric is a Sass variable */
        stroke-dasharray: 1;
        stroke-dashoffset: 1;
        transition: stroke-dashoffset 0.6s cubic-bezier(0.7, 0, 0.3, 1);
    }

    .coolFunnyLink:hover path {
        stroke-dashoffset: 0;
        transition-timing-function: cubic-bezier(0.8, 1, 0.7, 1);
    }
}

.coolCircleEyeButton {
    position: relative;
    display: inline-block;
    padding-left: 20px;

    .textCircle {
        display: block;
        width: 250px;
        animation: rotate 7s linear infinite;
        webkit-animation: rotate 7s linear infinite;

        text {
            font-family: Arial, sans-serif;
            font-size: 20px;
            fill: white;
        }
    }

    @keyframes rotate {
        to {
            transform: rotate(-360deg);
            -webkit-transform: rotate(-360deg);
            -moz-transform: rotate(-360deg);
            -ms-transform: rotate(-360deg);
            -o-transform: rotate(-360deg);
        }
    }

    &:hover {
        text {
            font-weight: 600;

        }

        .eye__inner,
        .eye_iris,
        .eye__lashes-up {
            animation: hide 2s step-end infinite;
            -webkit-animation: hide 2s step-end infinite;

            @keyframes hide {
                0% {
                    opacity: 0;
                }

                10% {
                    opacity: 1;
                }

            }
        }

        .eye__lashes-down {
            animation: show 2s step-end infinite;
            -webkit-animation: show 2s step-end infinite;

            @keyframes show {
                0% {
                    opacity: 1;
                }

                10% {
                    opacity: 0;
                }

            }

        }
    }

}

.eye {
    position: absolute;
    width: 60px;
    height: 60px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    -webkit-transform: translate(-50%, -50%);
    -moz-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    -o-transform: translate(-50%, -50%);

    &__outer,
    &__inner,
    &__lashes-up,
    &__lashes-down {
        stroke: #000000;
        fill: none;
        stroke-width: 1.5px;
    }

    &__lashes-down {
        opacity: 0;
    }
}

@media (max-width:1350) {
    .projects {
        padding: 3rem;
    }

    .project__img img {
        width: 74vw;
        right: 5rem;
    }

    .project__infos--name {
        font-size: 38px;


    }

    .project .coolCircleEyeButton {
        transform: translate(-14rem, -3rem);
        -webkit-transform: translate(-14rem, -3rem);
        -moz-transform: translate(-14rem, -3rem);
        -ms-transform: translate(-14rem, -3rem);
        -o-transform: translate(-14rem, -3rem);
    }

    @media (max-width:740px) {
        .projects {
            padding: 1rem;

        }

        .project__tags {
            flex-direction: row;
            top: 0;
            left: 0;
        }
    }

    .project__infos--name {
        font-size: 35px;

        span {
            display: none;
        }

        .project__img img {
            width: 85vw;
            right: 3rem;

        }



    }

    @media (max-width:440px) {
        .projects .coolCircleEyeButton {
            transform: translate(0);
            -webkit-transform: translate(0);
            -moz-transform: translate(0);
            -ms-transform: translate(0);
            -o-transform: translate(0);
        }

        .project__tags {
            gap: 6px;
            font-size: 14px;

        }

        .project__header span {
            font-size: 25px;
        }

    }

}

.project {
    .threeop {
        canvas {
            width: 60vw;
            height: 400px;
            position: absolute;
        }
    }

}

.project canvas {
    position: absolute;
    height: 600px;
    width: 400px;

}
