*{
    margin: 0;
    box-sizing: border-box;
    &::after
    &::before{
        box-sizing: border-box;
    }
}
html,
body{
    overflow-x: hidden;
}
body{
    font-family: "Poppins", sans-serif;
    font-weight: 400;
    font-style: normal;
    line-height: 1.5;
    -webkit-font-smoothing: antialiased;
    height: 100vw;
}

  
button,
input,
textarea,
select{
    font: inherit;

}
p,
h1,
h2,
h3,
h4,
h5,
h6{
    overflow-wrap: break-word;
}
img,
video,
canvas,
svg{
    // max-width: 100% ;
