$gold-color:#ffc60b;
$purple-electric:#CC00FF;
$light-gold-color:#32f750;
$red-color:#f44336;
$blue-color:#2196f3;

$darkish-blue: #000009;
$box-shadow-blue1:rgba(31,38,135,0.27);
